 #include<fstream> #include<iostream> #include<ctype.h> using namespace std; void remove_single_comment(char source[]), char dest[]); void remove_multi_comment(char source[], char dest[]); int main() { ifstream fin; ofstream fout; char ch; int count=0; remove_comment("compress_file.cpp","temp.cpp"); fin.open("compress_file.cpp"); fout.open("temp.cpp"); while(!fin.eof()){ fin.get(ch); if(ch=='\n'||ch=='\r'||ch=='\t') fout<<' '; else fout<<ch; } fin.close(); fout.close(); fin.open("temp.cpp"); fout.open("compressed.cpp"); while(!fin.eof()) { fin.get(ch); if(isspace(ch)) count++; else count=0; if(count<=1) fout<<ch; } fin.close(); fout.close(); remove("temp.cpp"); cout<<"\n\n\n File Compression Complete..."; return 0; } void remove_single_comment(char source[], char dest[]){ ifstream fin(source); ofstream fout(dest); char ch; while(!fin.eof()) { fin.get(ch); if(ch=='/') { fin.get(ch); if(ch=='/') { while(!fin.eof()) { fin.get(ch); if(ch=='\n'||ch=='\r') break; } } else fout<<"/"<<ch; } else fout<<ch; } fin.close(); fout.close(); } void remove_multi_comment(char source[], char dest[]){ ifstream fin(source); ofstream fout(dest); char ch; while(!fin.eof()) { fin.get(ch); if(ch=='/') { fin.get(ch); if(ch=='*') while(!fin.eof()) { fin.get(ch); if(ch=='/') break; } } else fout<<ch; } fin.close(); fout.close(); } 