 #include<fstream> #include<iostream> using namespace std; struct student{ int roll; char name[30]; char address[80]; }; int main() { ifstream fin("student.dat"); ofstream fout("temp.dat"); student s; int troll; cout<<"\n Enter Roll no That you want to remove :"; cin>>troll; while(fin.read((char*)&s,sizeof(student))) { if(s.roll==troll) { cout<<"\n Enter New Name :"; cin>>s.name; cout<<"\n Enter New address :"; cin>>s.address; } fout.write((char*)&s,sizeof(student)); } fin.close(); fout.close(); remove("student.dat"); rename("temp.dat","student.dat"); cout<<"\n Record Deleted Successfully"; return 0; } 