 #include<fstream> #include<iostream> using namespace std; void remove_multi_comment(char source[], char dest[]){ ifstream fin(source); ofstream fout(dest); char ch; while(!fin.eof()) { fin.get(ch); if(ch=='/') { fin.get(ch); if(ch=='*') while(!fin.eof()) { fin.get(ch); if(ch=='/') break; } } else fout<<ch; } fin.close(); fout.close(); } int main() { char ch; remove_multi_comment("remove_multi_line_comment.cpp","output.cpp"); ifstream fin("output.cpp"); while(fin.get(ch)) cout<<ch; fin.close(); return 0; } 