 #include<iostream> #include<fstream> using namespace std; void remove_single_comment(char source[], char dest[]){ ifstream fin(source); ofstream fout(dest); char ch; while(!fin.eof()) { fin.get(ch); if(ch=='/') { fin.get(ch); if(ch=='/') { while(!fin.eof()) { fin.get(ch); if(ch=='\n'||ch=='\r') break; } } else fout<<"/"<<ch; } else fout<<ch; } fin.close(); fout.close(); } int main() { char source[]="remove_single_line_comment.cpp"; char dest[]="temp.cpp"; remove_single_comment(source,dest); ifstream fin(dest); char ch; while(fin.get(ch)) cout<<ch; fin.close(); return 0; } 