 #include<iostream> #include<conio.h> using namespace std; int binary_search(int x[],int n, int data) { int found =0; int first,last,mid; first=0,last=n-1; while(first<=last && found ==0) { mid = (first+last)/2; if(x[mid]==data) found =1; else if(x[mid]<data) first = mid+1; else last = mid-1; } return found; } int main() { int x[10],i,data,result; for(i=0;i<10;i++) { cout<<"\n Enter any no :"; cin>>x[i]; } cout<<"\n Enter number that you want to search : "; cin>>data; result = binary_search(x,10,data); if (result==1) cout<<"\n Data available in this list"; else cout<<"\n Data not available in this list"; return 0; } 