 #include<iostream> #include<iomanip> #include<conio.h> #define SIZE 5 using namespace std; class cir_queue{ int queue[SIZE],front,rear; public: cir_queue(){ front = rear = -1; } void add_element(int value); void delete_element(); void display(); }; int main() { int choice, value; cir_queue q; do{ cout<<"\n Circular Queue Menu"; cout<<"\n1. Add Elelement"; cout<<"\n2. Delete Element"; cout<<"\n3. Display"; cout<<"\n4. Exit"; cout<<"\n Enter your choice (1..4) :"; cin>>choice; switch(choice) { case 1: cout<<"\nEnter the value to be insert: "; cin>>value; q.add_element(value); break; case 2: q.delete_element(); break; case 3: q.display(); break; case 4: break; default: printf("\nPlease select the correct choice!!!\n"); } }while(choice!=4); } void cir_queue::add_element(int value) { if((front == 0 && rear == SIZE - 1) || (front == rear+1)) cout<<"\nCircular Queue is Full! Insertion not possible!!!\n"; else{ if(rear == SIZE-1 && front != 0) rear = -1; queue[++rear] = value; cout<<"\nInsertion Success!!!\n"; if(front == -1) front = 0; } } void cir_queue::delete_element() { if(front == -1 && rear == -1) cout<<"\nCircular Queue is Empty! Deletion is not possible!!!\n"; else{ cout<<"\nDeleted element : %d\n"<<queue[front++]; if(front == SIZE) front = 0; if(front-1 == rear) front = rear = -1; } } void cir_queue::display() { if(front == -1) cout<<"\nCircular Queue is Empty!!!\n"; else{ int i = front; cout<<"\nCircular Queue Elements are : \n"; if(front <= rear){ while(i <= rear) cout<<setw(6)<<queue[i++]; } else{ while(i <= SIZE - 1) cout<<setw(6)<<queue[i++]; i = 0; while(i <= rear) cout<<setw(6)<<queue[i++]; } } return; } 