 #include<iostream> #include<iomanip> #include<conio.h> using namespace std; void delete_element(int x[],int &m,int pos) { int i; if(m<=0) cout<<"\n Array Empty. Deletion of element is not possible"; else { for(i=pos-1;i<m;i++) x[i]=x[i+1]; m--; } return; } int main() { int x[10],i,m,pos,data; cout<<"\n Enter array size less than (10) :"; cin>>m; for(i=0;i<m;i++) { cout<<"\n Enter any no :"; cin>>x[i]; } cout<<"\n Enter position where you want to delete :"; cin>>pos; delete_element(x,m,pos); cout<<"\n Array after Deleted element : "; for(i=0;i<m;i++) cout<<setw(5)<<x[i]; return 0; } 