 #include<iostream> #include<iomanip> #include<conio.h> using namespace std; void insert_element(int x[],int n, int &m,int pos, int data) { int i; if(m>=n) cout<<"\n Array full. Insertion of element is not possible"; else { for(i=m;i>=pos;i--) x[i+1]=x[i]; x[pos]=data; m++; } return; } int main() { int x[10],i,m,pos,data; cout<<"\n Enter array size less than (10) :"; cin>>m; for(i=0;i<m;i++) { cout<<"\n Enter any no :"; cin>>x[i]; } cout<<"\n Enter position where you want to insert your element :"; cin>>pos; cout<<"\n Enter data :"; cin>>data; insert_element(x,10,m,pos,data); cout<<"\n Array after inserted element : "; for(i=0;i<m;i++) cout<<setw(5)<<x[i]; return 0; } 