 #include<iostream> #include<iomanip> #include<conio.h> using namespace std; class queue { int x[10],data,front,rear; public: queue(){ front = rear = -1;} void add_element(); void delete_element(); void display(); }; void queue::add_element(){ if(rear>=9) cout<<"\n Queue overflow"; else { cout<<"\n Enter data : "; cin>>data; if(rear==-1) front = rear = 0; else rear = rear+1; x[rear]=data; } } void queue::delete_element(){ if(front==-1) cout<<"\n Queue underflow...."; else { data= x[front]; if(front == rear) front = rear = -1; else front = front+1; cout<<"\n Deleted Element : "<<data; } } void queue::display(){ if(front == -1) cout<<"\n queue underflow....."; else { for(int i=front;i<=rear;i++) cout<<setw(4)<<x[i]; } } int main() { int choice; queue q; do { cout<<"\n Queue Menu \n"; cout<<"\n1. Add Element "; cout<<"\n2. Delete Element "; cout<<"\n3. Display Queue"; cout<<"\n4. Exit"; cout<<"\n Enter your choice (1..4) : "; cin>>choice; switch(choice) { case 1: q.add_element();break; case 2: q.delete_element();getch();break; case 3: q.display();getch();break; case 4: break; default: cout<<"\n wrong choice...try again"; } }while(choice!=4); return 0; } 