 #include<iostream> #include<iomanip> #include<conio.h> using namespace std; class stack { int x[10],m,data; public: stack(){ m=0;} void push(); void pop(); void display(); }; void stack::push(){ if(m>=10) cout<<"\n Stack overflow"; else { cout<<"\n Enter data :"; cin>>data; x[m]=data; m++; } } void stack::pop(){ if(m<=0) cout<<"\n Stack underflow...."; else { data= x[m-1]; m= m-1; cout<<"\n Poped value :"<<data; } } void stack::display(){ if(m<=0) cout<<"\n Stack underflow....."; else { for(int i=m-1;i>=0;i--) cout<<setw(4)<<x[i]; } } int main() { int choice; stack s; do { cout<<"\n Stack Menu \n"; cout<<"\n1. Push "; cout<<"\n2. Pop "; cout<<"\n3. Display"; cout<<"\n4. Exit"; cout<<"\n Enter your choice (1..4) : "; cin>>choice; switch(choice) { case 1: s.push();break; case 2: s.pop();getch();break; case 3: s.display();getch();break; case 4: break; default: cout<<"\n wrong choice...try again"; } }while(choice!=4); return 0; } 