 #include<iostream> #include<iomanip> using namespace std; int binary_count(int n) { int count=0; int rem; while(n!=0) { rem = n%2; if ( rem ==1) count++; n = n/2; } return(count); } int main() { int n,one,res,count=0;; cout<<"\n Enter any number N :"; cin>>n; one = binary_count(n); for(int i=1;i<n;i++) { res = binary_count(i); if(res==one) count++; } cout<<"\n "<<n << " is number "<<count <<" in the sequence of number "<<one <<" 1-bits"; return 0; } 