 #include<iostream> #include<iomanip> using namespace std; int main() { int n,i,j,k,star; cout<<"\n Enter any integer no :"; cin>>n; for(i=1;i<=n;i++) { for(k=1;k<=n-i;k++) cout<<" "; star=1; for(j=1;j<=2*i-1;j++) { if(star==1|| star==2*i-1) cout<<"*"; else cout<<" " ; star++; } cout<<"\n"; } for(i=n-1;i>=1;i--) { for(k=1;k<=n-i;k++) cout<<" "; star=1; for(j=1;j<=2*i-1;j++) { if(star==1|| star==2*i-1) cout<<"*"; else cout<<" " ; star++; } cout<<"\n"; } return 0; } 