 #include<iostream> #include<iomanip> using namespace std; void magicSquare(int n) { int x[n][n]; int i,j; for(i=0;i<n;i++) for(j=0;j<n;j++) x[i][j]=0; i = n/2; j = n-1; for (int num=1; num <= n*n; ) { if (i==-1 && j==n) { j = n-2; i = 0; } else { if (j == n) j = 0; if (i < 0) i=n-1; } if (x[i][j]) { j -= 2; i++; continue; } else x[i][j] = num++; j++; i--; } for(i=0; i<n; i++) { for(j=0; j<n; j++) cout<<setw(4)<<x[i][j]; cout<<endl; } } int main() { int n; cout<<"Enter any odd number between 3 to 9 :"; cin>>n; magicSquare(n); return 0; } 