 #include<iostream> #include<iomanip> using namespace std; void magicSquare(int n){ int x[n][n]={0}; int row = 0, col = n / 2, i, j, square = n * n; for( i=1; i <= square ; i++ ){ x[ row ][ col ] = i; if( i % n == 0 ) row++; else{ if( row == 0 ) row = n - 1; else row--; if( col == ( n - 1 ) ) col = 0; else col++; } } for(i=0; i<n; i++) { for(j=0; j<n; j++) cout<<setw(4)<<x[i][j]; cout<<endl; } } int main() { int n; cout<<"Enter any odd number between 3 to 9 :"; cin>>n; magicSquare(n); return 0; } 