 #include<iostream> #include<conio.h> #include<string.h> #include<iomanip> using namespace std; int main() { char string[100]; cout<<"\n Enter any string : "; cin.getline(string,100); int count=0; int i; for(i=0;i<strlen(string);i++) { if(string[i]=='(') count++; if(string[i]==')') count--; } if(count==0) cout<<"\n String have matching braces."; else cout<<"\n String does not have matching braces."; return 0; } 