 #include<iostream> #include<iomanip> #include<conio.h> using namespace std; int main() { int n,i,j,k,l; cout<"\n Enter any number n : "; cin>>n; cout<<" Number Rhombus \n"; for(i=1;i<=n;i++) { for(j=1;j<=n-i;j++) cout<<setw(3)<<' '; for(k=1;k<=i;k++) cout<<setw(3)<<k; for(l=i-1;l>=1;l--) cout<<setw(3)<<l; cout<<endl; } for(i=n-1;i>=1;i--) { for(j=1;j<=n-i;j++) cout<<setw(3)<<' '; for(k=1;k<=i;k++) cout<<setw(3)<<k; for(l=i-1;l>=1;l--) cout<<setw(3)<<l; cout<<endl; } return 0; } 