 #include<iostream> #include<conio.h> #include<string.h> #include<iomanip> using namespace std; int main() { char str[100]; int i,j,k; cout<<"\n Enter any string : "; cin>>str; for(i=0;i<strlen(str);i++) { for(j=1;j<strlen(str)-i;j++) cout<<setw(2)<<""; for(k=0;k<=i;k++) cout<<setw(2)<<str[k]; cout<<endl; } for(i=0;i<strlen(str);i++) { for(j=i+1;j<strlen(str);j++) cout<<setw(2)<<str[j]; cout<<endl; } return 0; } 