 #include<iostream> #include<string.h> #include<conio.h> using namespace std; int main() { char str[100],str1[150]; int i,j,k,pos; cout<<"\n Enter any word :"; cin>>str; if(str[0]=='a'||str[0]=='e'||str[0]=='i'||str[0]=='o'||str[0]=='u') { strcat(str1,str); strcat(str1,"way"); } else { for(i=0;i<strlen(str);i++) { if(str[i]=='a'||str[i]=='e'||str[i]=='i'||str[i]=='o'||str[i]=='u') { pos = i; break; } } k=0; for(j=pos;j<strlen(str);j++) { str1[k++]=str[j]; } for(j=0;j<pos;j++) str1[k++]=str[j]; str1[k]='\0'; strcat(str1,"ay"); } cout<<str1; return 0; } 