 #include <iostream> #include<stdio.h> #include <string.h> #define MAX_SIZE 100 void removeAll(char * string, char * toRemove); int main() { char string[MAX_SIZE]; char toRemove[MAX_SIZE]; printf("Enter any string: "); gets(string); printf("Enter word to remove: "); gets(toRemove); printf("String before removing '%s' : %s\n", toRemove, string); removeAll(string, toRemove); printf("String after removing '%s' : %s\n", toRemove, string); return 0; } void removeAll(char * string, char * toRemove) { int i, j, stringLen, toRemoveLen; int found; stringLen = strlen(string); toRemoveLen = strlen(toRemove); for(i=0; i<stringLen - toRemoveLen; i++) { found = 1; for(j=0; j<toRemoveLen; j++) { if(string[i + j] != toRemove[j]) { found = 0; break; } } if(found == 1) { for(j=i; j<stringLen - toRemoveLen; j++) { string[j] = string[j + toRemoveLen]; } string[j] = '\0'; stringLen = stringLen - toRemoveLen; } } } 