 #include <stdio.h> #include<iostream> #include<conio.h> #include <string.h> using namespace std; int main() { int count = 0, c = 0, i, j = 0, k, l, space = 0; char str[100], p[50][100], str1[20], ptr1[50][100], cmp[50]; cout<<"Enter the string : "; cin.getline(str,100); for (i = 0;i < strlen(str);i++) { if ((str[i] == ' ')||(str[i] == ', ')||(str[i] == '.')) { space++; } } for (i = 0, j = 0, k = 0;j < strlen(str);j++) { if ((str[j] == ' ')||(str[j] == ',')||(str[j] == '.')) { p[i][k] = '\0'; i++; k = 0; } else p[i][k++] = str[j]; } for (i = 0;i < space;i++) { for (j = i + 1;j <= space;j++) { if ((strcmp(p[i], p[j]) > 0)) { strcpy(cmp, p[i]); strcpy(p[i], p[j]); strcpy(p[j], cmp); } } } cout<<"\n After sorting string is : "; for (i = 0;i <= space;i++) cout<<p[i] <<" "; getch(); return 0; } 